import { useEffect, useState } from "react";

const Resturant =  () => {
    const [orders, setOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [httpError, setHttpError] = useState();
  

    // get orders from firebase and display them
    useEffect(() => {
        // fetchig data from database
        // fetchig data from database
    const fetchOrders = async () => {
        // getting response from database
        const response = await fetch(
          'https://himeals-default-rtdb.firebaseio.com/orders.json'
        );
  
        if (!response.ok) {
          throw new Error('Something went wrong!');
        }
  
        const responseData = await response.json();
  
        const loadedMeals = [];
        //  looping through the data and storing it in loadedMeals array
        for (const key in responseData) {
          loadedMeals[0].push({
            id: key,
            order: responseData[key].order,
            name: responseData[key].name,
            // description: responseData[key].description,
            price: responseData[key].price,
          });
        }
  
        setOrders(loadedMeals);
        setIsLoading(false);
        // display orders
        
         console.log(loadedMeals[0]);
      };
  
      fetchOrders().catch((error) => {
        setIsLoading(false);
        setHttpError(error.message);
      });
    },
    
    [])
    if (isLoading) {
      return (
        <section >
          <p>Loading...</p>
        </section>
      );
    }
  
    if (httpError) {
      return (
        <section>
          <p>{httpError}</p>
        </section>
      );
    }

    return(
        <div>
            <h1>Orders</h1>
            <ul>
                {orders.map((order) => (
                    <li key={order.id}>
                        <h2>{order.id}</h2>
                        <p>{order.description}</p>
                        <p>{order.price}</p>
                    </li>
                ))}
            </ul>
        </div>

    )

}

export default Resturant;
