      // for (let i = 0; i < loadedMeals.length; i++) {
      //   console.log(loadedMeals[i].order);
      // }
    };

    fetchOrders().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  },

    [])
  if (isLoading) {
    return (
      <section >
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section>
        <p>{httpError}</p>
      </section>
    );
  }
  // *****************************************************************************


  // orders.map = (order) => {
