      return (
        <>
          {/* <h3>{orders[i].order}</h3> */}
          console.log(orders[i])
        </>
      )
      // console.log(loadedMeals[i].order);
    }

  }

  // ****************************************************************

  return (
    <div>
      <h1>Orders</h1>
      <h3>{orders[0].name.name}</h3>
      {
        orders.map((order) => {
          return (
            <div key={order.id}>
              <h1>{console.log(order)}</h1>
              <h1>{order.id} </h1>
              <h2>{order.name.name} </h2>
              <h3> {order.name.city}</h3>

            </div>
          )
        })
      }



      {/* Loop through the orders array and get individual attributes */}
      {/* use for loop to loop through the array */}
      {/* <ul> */}
      {/* {objectList} */}


      {/* {console.log(orders)} */}
