import { Fragment, useState } from 'react';
import { Container } from 'react-bootstrap';
import HeaderCartButton from './HeaderCartButton';
import mealsImage from '../../assets/meals.jpg';
import classes from './Header.module.css';
import { FaHamburger } from 'react-icons/fa';
import Modal from '../UI/Modal';
import SignUp from './SignUp';
// import { useAuth } from '../../store/auth-context';
import {AuthProvider, useAuth } from '../../store/auth-context';

const Header = (props) => {
  const [error, setError] = useState("")
  const [showSignInpage, setShowSignInPage] = useState(false);
  const {currentUser,logout} = useAuth();



  const closeHandler = () => {
    setShowSignInPage(false);
  }

  async function handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/login")
    } catch {
      setError("Failed to log out")
    }
  }

  if (showSignInpage == true) {
    return (
      <AuthProvider>

        <Container className="d-flex align-items-center justify-content-center"
          style={{ minHeight: "100vh" }}>
          <div className="w-100" style={{ maxWidth: "400px" }}>
            <SignUp onClose={closeHandler} />
          </div>

        </Container>
      </AuthProvider>
    )
  }


  const showSignIn = () => {
    setShowSignInPage(true);

  };





  return (
    <Fragment>

      <header className={classes.header}>
        <FaHamburger />
        <h1>HiMeals</h1>
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes['main-image']}>
        <nav className={classes['sideNav-container']} >
          <div>
            {/* if user is signed in show signup button or logout button */}
          {currentUser ? <button onClick={handleLogout}> Logout</button> : <button onClick={showSignIn}> SignIn</button>}

            {/* <button onClick={showSignIn}> SignIn</button>
            <button> Logout</button> */}
          </div>
          <ul>
            <li><a href="#">Hungry</a></li>
            <li><a href="#">Sides</a></li>
            <li><a href="#">Drinks</a></li>
            <li><a href="#">Dessert</a></li>
          </ul>

        </nav>
        <img src={mealsImage} alt='A table full of delicious food!' />
      </div>

    </Fragment>
  );
};

export default Header;
