import { useEffect, useState } from "react";
import classes from './Resturant.module.css';

const Resturant = () => {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();


  // get orders from firebase and display them
  useEffect(() => {
    // fetchig data from database
    // fetchig data from database
    const fetchOrders = async () => {
      // getting response from database
      const response = await fetch(
        'https://himeals-default-rtdb.firebaseio.com/orders.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedMeals = [];
      //  looping through the data and storing it in loadedMeals array
      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          order: responseData[key].orderedItems,
          name: responseData[key].user,
          // description: responseData[key].description,
          price: responseData[key].price,
        });
      }

      setOrders(loadedMeals);
      setIsLoading(false);
      // console.log(orders[0].name);
      // display orders

      //  console.log(loadedMeals[0].name.name);
      //  Loop through the orders and display individual array

      //  console.log(loadedMeals[0].order[0].name);
      // for (let i = 0; i < loadedMeals.length; i++) {
      //   console.log(loadedMeals[i].order);
      // }
    };

    fetchOrders().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  },

    [])
  if (isLoading) {
    return (
      <section >
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section>
        <p>{httpError}</p>
      </section>
    );
  }

  // delete order from firebase and remove it from the page
  const orderComplete = async (id) => {

    console.log(id);
    console.log(orders);
                      // delete the order from the list 

    // const newOrders = orders.filter((order) => order.id !== id);
    // setOrders(newOrders);

                        // delete the order from the database

    // const response = await fetch(
    //   `https://himeals-default-rtdb.firebaseio.com/orders/${id}.json`,
    //   {
    //     method: 'DELETE',
    //   }
    // );
  };







  // *****************************************************************************


  // orders.map = (order) => {
  //   console.log()
  // }
  // mapping through the orders array and displaying the orders


  // Object.values(orders).map((order, index) => {
  //   console.log(order.order);
  //   return (
  //     <div key={index}>
  //       <h1>{console.log(order)}</h1>
  //       <h1>{order} </h1>
  //     </div>
  //   )
  // });


  // ****************************************************************

  return (
    <>
      <nav className={classes.navbar}>
        <span className={classes.header}>
          Orders:
        </span>
      </nav>
      <div >
        {/* <h1>Orders</h1> */}
        {/* <h3>{orders[0].name.name}</h3> */}
        {
          orders.map((order) => {
            return (
              <>
                <div className={classes.card}>
                  <div className={classes.cardItems} key={order.id}>
                    {console.log(order)}
                    {/* <h3>{order.id} </h3> */}
                    <h1>{order.name.name} </h1>
                    {/* <h3> {order.name.city}</h3> */}
                    {/* map array of orders here */}
                    {
                      order.order.map((item) => {
                        return (
                          <div key={item.id}>
                            <h1>{item.name} x {item.amount}</h1>
                          </div>
                        );
                      })
                    };
                  </div>
                  <div>
                    <button className={classes.flagButton} onClick={() => orderComplete(order.id)}>
                      hello
                    </button>
                  </div>
                </div>
              </>
            );
          })
        }
      </div>
    </>
  )


}

export default Resturant;
