import { useEffect } from 'react'; 
import classes from '../Cart/CartItem.module.css';

const Resturant = (props) => {

    useEffect(() => {
        // fetchig data from database
        const fetchMeals = async () => {
          // getting response from database
          const response = await fetch(
            'https://himeals-default-rtdb.firebaseio.com/meals.json'
          );
    
          if (!response.ok) {
            throw new Error('Something went wrong!');
          }
    
          const responseData = await response.json();
    
          const loadedMeals = [];
    //  looping through the data and storing it in loadedMeals array
          for (const key in responseData) {
            loadedMeals.push({
              id: key,
              name: responseData[key].name,
              description: responseData[key].description,
              price: responseData[key].price,
            });
          }
    
          setMeals(loadedMeals);
          setIsLoading(false);
        };
    
        fetchMeals().catch((error) => {
          setIsLoading(false);
          setHttpError(error.message);
        });

    }, []);

    return (
        <li className={classes['cart-item']}>
      <div>
        <h2>{props.name}</h2>
        <div className={classes.summary}>
          {/* <span className={classes.price}>The price is 10</span> */}
          <span className={classes.amount}> Number of food ordered is 2</span>
        </div>
      </div>
      <div className={classes.actions}>
        <button onClick={props.onRemove}>âœ…  </button>
        {/* <button onClick={props.onAdd}>+</button> */}
      </div>
    </li>
    );
    }

export default Resturant;
